    @Override
    protected void onResume()
    {
        super.onResume();
        getWindow().getDecorView().setSystemUiVisibility(
                View.SYSTEM_UI_FLAG_LAYOUT_STABLE
                        | View.SYSTEM_UI_FLAG_LAYOUT_HIDE_NAVIGATION
                        | View.SYSTEM_UI_FLAG_LAYOUT_FULLSCREEN
                        | View.SYSTEM_UI_FLAG_HIDE_NAVIGATION
                        | View.SYSTEM_UI_FLAG_FULLSCREEN
                        | View.SYSTEM_UI_FLAG_IMMERSIVE_STICKY);
    }

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);

        requestWindowFeature(Window.FEATURE_NO_TITLE);

        getWindow().getDecorView().setSystemUiVisibility(
                View.SYSTEM_UI_FLAG_LAYOUT_STABLE
                        | View.SYSTEM_UI_FLAG_LAYOUT_HIDE_NAVIGATION
                        | View.SYSTEM_UI_FLAG_LAYOUT_FULLSCREEN
                        | View.SYSTEM_UI_FLAG_HIDE_NAVIGATION
                        | View.SYSTEM_UI_FLAG_FULLSCREEN
                        | View.SYSTEM_UI_FLAG_IMMERSIVE_STICKY);

        setContentView(R.layout.activity_main);

        rlayout = (RelativeLayout) findViewById(R.id.mainlayout);
        textView = (TextView)findViewById(R.id.textView);
        ib = (ImageButton)findViewById(R.id.imageButton);

        final Random ran = new Random();
        rlayout.setOnClickListener(new View.OnClickListener() {

            @Override
            public void onClick(View v) {

                if ( oo == 0 )
                {
                    random = ran.nextInt(500);
                    textView.setText(words[random].split("#")[0]);
                    textView.setTypeface(null, Typeface.BOLD);
                    if(dark)
                        textView.setTextColor(Color.WHITE);
                    else
                        textView.setTextColor(Color.BLACK);
                    oo = 1;
                }
                else if ( oo == 1)
                {

                    //textView.setTypeface(null, Typeface.NORMAL);
                    String sourceString = "<b><u>" + words[random].split("#")[0] + "</b></u> " + "<br>" + "<i>"  + words[random].split("#")[1] + "</i>";
                    //textView.setText(Html.fromHtml(sourceString));

                    textView.setTypeface(null, Typeface.ITALIC);
                    textView.setText(words[random].split("#")[1]);
                    if(dark)
                        textView.setTextColor(Color.WHITE);
                    else
                        textView.setTextColor(Color.BLACK);

                    oo = 0;
                }

            }

        });
    }
 public void darkmode(View v)
 {

     if (!dark)
     {
         ib.setImageDrawable(getResources().getDrawable(R.drawable.ic_black));
         ib.setImageAlpha(255);

         View someView = findViewById(R.id.textView);
         View root = someView.getRootView();
         root.setBackgroundColor(getResources().getColor(android.R.color.black));

         dark = true;
     }

     else if (dark)
     {
         ib.setImageDrawable(getResources().getDrawable(R.drawable.ic_white));
         ib.setImageAlpha(150);

         View someView = findViewById(R.id.textView);
         View root = someView.getRootView();
         root.setBackgroundColor(getResources().getColor(android.R.color.white));

         dark = false;
     }

     if(dark)
         textView.setTextColor(Color.WHITE);
     else
         textView.setTextColor(Color.BLACK);
 }


